#local-services-results {
  display: none;
}

#local-services-results.active {
  display: block;
  position: relative;
  width: 100%;
  padding: 1em;
  background-color: #444;
}
#local-services-results.active .local-address {
  color: #fff;
  padding-top: 0.5em;
  border-bottom: 0.25em solid #E48F22;
  margin-bottom: 1em;
}
#local-services-results.active .local-content {
  column-count: 1;
  color: #fff;
}
#local-services-results.active .local-content .info-section {
  margin-bottom: 1em;
}
#local-services-results.active .local-content .info-section span {
  background-color: #E48F22;
  padding: 0.5em;
  font-weight: bold;
}
#local-services-results.active .local-content .info-section div {
  color: #fff;
  background-color: #515151;
  padding: 0.5em;
  margin-top: 1em;
}
#local-services-results.active .local-content .info-section div h5 {
  border-bottom: 0.1em solid;
}
#local-services-results.active .local-content .info-section div p {
  margin: 0;
}
#local-services-results.active .local-content .info-section div p strong {
  font-weight: bold;
}
#local-services-results.active .local-content .info-section div p a {
  color: #fff;
}
#local-services-results.active .local-content .info-section h4 a {
  color: #CF3234;
}
#local-services-results.active .loader-container {
  display: flex;
}
#local-services-results.active .loader-container article {
  margin: auto;
}
#local-services-results.active #close-services-btn {
  position: absolute;
  top: 0;
  right: 0;
  width: 3em;
  height: 3em;
  display: flex;
  background-color: #CF3234;
  color: #fff;
  cursor: pointer;
  transition: all 500ms cubic-bezier(0.64, 0.09, 0.08, 1);
}
#local-services-results.active #close-services-btn span {
  margin: auto;
}
#local-services-results.active #close-services-btn:hover {
  width: 3.25em;
  height: 3.25em;
  transition: all 500ms cubic-bezier(0.64, 0.09, 0.08, 1);
}

@media all and (min-width: 551px) {
  #local-services-results.active .local-content {
    column-count: 2;
  }
}
@media all and (min-width: 1024px) {
  #local-services-results.active .local-content {
    column-count: 3;
  }
}
@media all and (min-width: 1401px) {
  #local-services-results.active .local-content {
    column-count: 4;
  }
}
